var sw_timeout;var show={message:function(a,b){if(String(a).search(/^\[OK\]\s{1}.{1,}/)!='-1'){show.exito(this.get_mensaje(a),3000);if(typeof b!='undefined'){sw_timeout=setTimeout(function(){window.location=b},2000)}return true}else{if(String(a).search(/^\[ERROR\]\s{1}.{1,}/)!='-1'){show.error(this.get_mensaje(a),4000);return false}else{show.notificacion(this.get_mensaje(a),4000);return false}}},error:function(a,b){$("#auxiliar").html(this.get_mensaje(a)).slideDown("fast").removeClass('notice').removeClass('success').addClass("error").animate({opacity:'0.5'},'fast',function(){$(this).animate({opacity:'1'},'fast')});if(arguments.length==2){show.clear();sw_timeout=setTimeout(function(){$("#auxiliar").slideUp("fast",function(){$(this).html('').removeClass("error")})},arguments[1]);return sw_timeout}},notificacion:function(a,b){$("#auxiliar").html(this.get_mensaje(a)).slideDown("fast").removeClass('error').removeClass('success').addClass("notice").animate({opacity:'0.5'},'fast',function(){$(this).animate({opacity:'1'},'fast')});if(arguments.length==2){show.clear();sw_timeout=setTimeout(function(){$("#auxiliar").slideUp("fast",function(){$(this).html('').removeClass("notice")})},arguments[1]);return sw_timeout}},exito:function(a,b){$("#auxiliar").html(this.get_mensaje(a)).slideDown("fast").removeClass('error').removeClass('notice').addClass("success").animate({opacity:'0.5'},'fast',function(){$(this).animate({opacity:'1'},'fast')});if(arguments.length==2){show.clear();sw_timeout=setTimeout(function(){$("#auxiliar").slideUp("fast",function(){$(this).html('').removeClass("success")})},arguments[1]);return sw_timeout}},get_mensaje:function(a){show.clear();sw_timeout=String(a).replace(/\[\D{1,}\]\s{1}(.)/,'$1');return sw_timeout},clear:function(){clearTimeout(sw_timeout)}}